DROP TABLE IF EXISTS monthly_inflation CASCADE;
DROP TABLE IF EXISTS inflation_cpi CASCADE;

CREATE TABLE monthly_inflation (
  id SERIAL PRIMARY KEY,
  date DATE UNIQUE NOT NULL,
  rate NUMERIC(10, 8) NOT NULL
);


CREATE TABLE inflation_cpi (
    id SERIAL PRIMARY KEY,
    date DATE UNIQUE NOT NULL,
    cpi_value DECIMAL(10,4) NOT NULL
);

